mapper.insert:
INSERT INTO user
SET uid = #{uid}
, upw = #{upw}
, nm = #{nm}
, pic = #{pic}

mapper.select:
SELECT user_id AS userId, uid, upw, nm, pic, created_at AS createdAt, updated_at AS updatedAt
FROM user
WHERE uid = #{uid}

mapper.update:
UPDATE user
SET upw = #{newPw}
WHERE user_id = #{userId}
